import React, { useState } from "react";
import loginImage from "./resources/images/login.png";
import "./styles/style.css";


function Login() {
  const [values, setValues] = useState({
    email: '',
    password: ''
  })
  
  return (
    <div className="container-fluid">
      <div className="row">
        <div
          className="col-7"
          style={{ backgroundImage: `url(${loginImage})`, height: "100vh" }}
        >
          {/* this part is left side of the division */}
        </div>
        <div
          className="col-5 d-flex align-items-center justify-content-center "
          style={{ height: "100vh" }}
        >
          {/* this part is right side of the division. All the login form will stay here */}

          <form action="">
            <h2>Sign In</h2>
            <p className="opacity-75">Please enter your login details to sign in</p>

            {/* Email input */}
            <div className="mb-3 ">
              <input
                type="email"
                className="form-control"
                placeholder="Enter email"
              ></input>
            </div>

            {/* password field */}
            <div className="mb-3">
              <input
                type="password"
                className="form-control"
                placeholder="Enter Password"
              />
            </div>

            {/* terms and policies */}
            <p className="opacity-75">You are agree to our terms and policies</p>

            {/* login button */}
            <div class="d-grid gap-2">
              <button class="login-button" type="button">
                Login
              </button>
                <p>
                   New User? 
                    <a  href="/signup"> Sign Up</a>
                </p>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Login;
